import React, { useState } from 'react';
import { getDocument, GlobalWorkerOptions } from 'pdfjs-dist';

// Set the workerSrc to the path of the worker file in the public folder
GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.js`;

function PDFToText() {
  const [pdfText, setPdfText] = useState('');

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'application/pdf') {
      const fileReader = new FileReader();
      fileReader.onload = async function () {
        const typedarray = new Uint8Array(this.result);

        const loadingTask = getDocument(typedarray);
        const pdf = await loadingTask.promise;
        let textContent = '';

        for (let pageNum = 1; pageNum <= pdf.numPages; pageNum++) {
          const page = await pdf.getPage(pageNum);
          const text = await page.getTextContent();
          const pageText = text.items.map((item) => item.str).join(' ');
          textContent += pageText + '\n';
        }

        setPdfText(textContent);
      };
      fileReader.readAsArrayBuffer(file);
    } else {
      alert('Please upload a valid PDF file.');
    }
  };

  return (
    <div>
      <h1>Upload a PDF to Extract Text</h1>
      <input type="file" onChange={handleFileChange} accept="application/pdf" />
      <pre>{pdfText}</pre>
    </div>
  );
}

export default PDFToText;
