import React, { useState } from 'react';
import axios from 'axios';
import './PdfToText.css'

function PDFUploader() {
  const [extractedInfo, setExtractedInfo] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setExtractedInfo(''); // Clear previous result
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a PDF file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', selectedFile);

    try {
      const response = await axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Remove stars from the extracted text
      let cleanedText = response.data.extractedInfo
        .replace(/\*\*/g, '') // Remove double asterisks
        .replace(/\*/g, '');  // Remove single asterisks

      setExtractedInfo(cleanedText);
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Failed to upload the file.');
    }
  };

  return (
    <div>
      <h1>Upload a PDF to Extract Text</h1>
      <input type="file" onChange={handleFileChange} accept="application/pdf" />
      <button onClick={handleUpload}>Upload and Extract Text</button>
      {extractedInfo && (
        <div>
          <h2>Gemini Model Result</h2>
          <pre>{extractedInfo}</pre>
        </div>
      )}
    </div>
  );
}

export default PDFUploader;
